# script to plot intensity vs wavelength with data generated by
# EQ_spectra.py.
# this is a very simple matplotlib script; for further functionality
# see the online matplotlib documentation.

import math
import numpy as np
import matplotlib.pyplot as plt

lam = np.loadtxt('intensity_spectra.txt', usecols=[0])
q_lambda = np.loadtxt('intensity_spectra.txt', usecols=[1])

fig = plt.figure()
fig.subplots_adjust(bottom=0.2, left=0.2)
ax1 = fig.add_subplot(111)
ax1.plot(lam, q_lambda)
ax1.set_title('X2 VUV spectra in air test case')

# x-axis properties
ax1.set_xlim(100.0, 200.0)
ax1.set_xlabel("Wavelength ($\mathrm{nm}$)")

# y-axis properties (uncomment to set)
#ax1.set_ylim(0.0, 1.0e13)
#ax1.set_yscale('log')
ax1.set_ylabel('Spectral irradiance, $\mathrm{q_\lambda}$ ($\mathrm{W}$ / $\mathrm{cm^2-sr-um}$)')

plt.savefig('intensity-spectra.eps')
plt.show()
